CREATE TABLE IF NOT EXISTS `user`
(
    `id`        int                             NOT NULL AUTO_INCREMENT,
    `username`  varchar(45) COLLATE utf8mb4_bin NOT NULL,
    `firstname` varchar(45) COLLATE utf8mb4_bin  DEFAULT NULL,
    `lastname`  varchar(45) COLLATE utf8mb4_bin  DEFAULT NULL,
    `password`  varchar(255) COLLATE utf8mb4_bin DEFAULT NULL,
    `roles`     varchar(255) COLLATE utf8mb4_bin DEFAULT NULL,
    PRIMARY KEY (`id`)
);

CREATE TABLE threat_level
(
    id   int not null AUTO_INCREMENT,
    name varchar(100),
    primary key (id)
);

INSERT INTO threat_level(name)
VALUES ('False alarm'),
       ('Low'),
       ('Medium'),
       ('High');


CREATE TABLE categorized_img
(
    id              int     not null AUTO_INCREMENT,
    filename        varchar(255),
    img             longtext,
    threat_level_id int     null default null,
    addressed       boolean null default null,
    sector          int,
    primary key (id),
    foreign key (threat_level_id) references threat_level (id)
);

CREATE TABLE model
(
    id       int not null AUTO_INCREMENT,
    filename varchar(45),
    created  datetime,
    accuracy varchar(25),
    graph    longtext,
    primary key (id)
);

INSERT INTO model(filename, created, accuracy, graph)
VALUES ('damageDetectionModel', now(), '0.9860',
        'data:image/png;base64,');

CREATE TABLE scan
(
    id          int not null AUTO_INCREMENT,
    `date`      datetime,
    model_id    int,
    scanned     int,
    cracked     int,
    non_cracked int,
    primary key (id),
    foreign key (model_id) references model (id)
);

